sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here

    var gdt = new GlideDateTime();
    var orderDate = new GlideDateTime(current.u_when_needed);
    var today = gdt.getLocalDate();
    var isToday = orderDate.getLocalDate();


	gs.addInfoMessage('todays date business rule: ');

    if (isToday.equals(today)) {


        gs.addErrorMessage('Same day deliveries are not supported. Sorry!');
		current.setAbortAction(true);
		current.setValue('u_when_needed', '');

    }


















})(current, previous);
