sys_script.script:
(function executeRule(current, previous /*null when async*/) {

// If the When needed date is before rightnow, do not write the record to the database. Output an error message to the screen
// Instantiate the NeedItUtils class. Call the isDatePast method and pass the u_when_needed value.
  var niutils = new NeedItUtils();
  var isPast = niutils.isDatePast(current.u_when_needed);
// If the isDatePast method returns true, the date is in the past.
  if(isPast == true){
    gs.addErrorMessage("When needed date cannot be in the past. Your request has not been saved to the database.");
    current.setAbortAction(true);
  }
// *** Without using script Include class ***
// rightnow stores the current time
// var rightnow = new GlideDateTime();
// // Create a GlideDateTime object for the When needed date
// var whenNeeded = new GlideDateTime(current.u_when_needed);
//  if(whenNeeded.before(rightnow)){
//    gs.addErrorMessage("When needed date cannot be in the past.  Your request has not been saved to the database.");
//    current.setAbortAction(true);
//  }

// Do not allow same-day requests
// Get the date portion of rightnow and whenNeeded (no timestamp)
	var isToday = niutils.isDateToday(current.u_when_needed);
// If the isDatePast method returns true, the date is today.
	if(isToday == true){
		gs.addErrorMessage("When needed date cannot be today, allow one day for processing. Your request has not been saved to the database.");
		current.setAbortAction(true);
	}

// // Without using Script Include NeedItUtils class	
//   var today = rightnow.getLocalDate();
//   var istoday = whenNeeded.getLocalDate();
// // Compare today and istoday to see if they are the same day
// 	  if(today == istoday) {
// // if(today.compareTo(istoday) == 0){
//     gs.addErrorMessage("Requests cannot be submitted on the same day.");
//     current.setAbortAction(true);
//   }

})(current, previous);
