sys_script_include.script:
var NeeditLeadTimeSet = Class.create();
NeeditLeadTimeSet.prototype = {
    initialize: function() {
    },
	setLeadtime: function(request_type)
	{
		var today = new GlideDateTime();

        var leadedtime;
        if (request_type == 'hr') {
            today.addDaysLocalTime(3);
            //leadedtime = today.getDisplayValue();
            return today;
        } else if (request_type == 'legal') {
            today.addDaysLocalTime(5);
            //leadedtime = today.getDisplayValue();
            return today;
        } else if (request_type == 'facilities') {
            today.addDaysLocalTime(1);
            //leadedtime = today.getDisplayValue();
            return today;
        } else {
            return 'nothing';
        }
	},

    type: 'NeeditLeadTimeSet'
};
