sys_script_include.script:
var NeeditLeadTime = Class.create();
NeeditLeadTime.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getLeadTime: function() {
        var today = new GlideDateTime();
		var request_type=this.getParameter('sysparm_requesttype');
        var leadedtime;
        if (request_type == 'hr') {
            today.addDaysLocalTime(3);
            //leadedtime = today.getDisplayValue();
            return today;
        } else if (request_type == 'legal') {
            today.addDaysLocalTime(5);
            //leadedtime = today.getDisplayValue();
            return today;
        } else if (request_type == 'facilities') {
            today.addDaysLocalTime(1);
            //leadedtime = today.getDisplayValue();
            return today;
        } else {
            return 'nothing';
        }
    },
    type: 'NeeditLeadTime'
});
