sys_ui_action.script:
function fetch() {
    var confirmation = confirm('Do you want to fetch all the incidents for the current caller?');
    if (confirmation) {
        gsftSubmit(null, g_form.getFormElement(), 'fetchincidents');
    } else if (!confirmation) {
        return;
    }

}
if (typeof window == 'undefined'&&current.u_requested_for.changes()) {
    restcall();
}

function restcall() {
    var request = new sn_ws.RESTMessageV2();
    request.setQueryParameter('sysparm_query', 'caller_id=' + current.u_requested_for);	
    request.setQueryParameter('sysparm_fields', 'number,category');
    //request.setQueryParameter('sysparm_limit',10);
	request.setLogLevel('all');
    request.setEndpoint('https://dev36074.service-now.com/api/now/table/incident');
    request.setHttpMethod('GET');

    //Eg. UserName="admin", Password="admin" for this code sample.
    var user = 'admin';
    var password = 'N@ni2023';

    request.setBasicAuth(user, password);
    request.setRequestHeader("Accept", "application/json");

    var response = request.execute();
    var responsebody = response.getBody();
    var objJSON = JSON.parse(responsebody);
    var incidents = '';

    for (var i = 0; i < objJSON.result.length; i++) {
        incidents = incidents + 'number: ' + objJSON.result[i].number + 'category: ' + objJSON.result[i].category;

    }
    current.description = incidents;
	current.short_description=objJSON.result[1].number;
    current.update();
}
