sys_ui_action.script:
(function() {

    try {
        var inputs = {};
        inputs['needit_task'] = current; // GlideRecord of table: x_58872_needit_needit_task 
        inputs['reason'] = 'Overdue task closed manually';


        // Start Asynchronously: Uncomment to run in background. Code snippet will not have access to outputs.
        // sn_fd.FlowAPI.getRunner().subflow('x_802222_needit_sp.handle_overdue_needit_tasks').inBackground().withInputs(inputs).run();

        // Execute Synchronously: Run in foreground. Code snippet has access to outputs.
        var result = sn_fd.FlowAPI.getRunner().subflow('x_802222_needit_sp.handle_overdue_needit_tasks').inForeground().withInputs(inputs).run();
        var outputs = result.getOutputs();

        // Get Outputs:
        // Note: outputs can only be retrieved when executing synchronously.
        var follow_up_task = outputs.follow_up_task; // Reference
        action.setRedirectURL(follow_up_task);
        action.setReturnURL(current);


    } catch (ex) {
        var message = ex.getMessage();
        gs.error(message);
    }

})();
