sys_web_service.script:
(function scriptedWebServiceOperation(request, response) {
    try {
        var gr = new GlideRecord('x_58872_needit_needit');
        gr.initialize();
        gr.setValue('short_description', request.short_description);
        gr.u_requested_for.setDisplayValue(request.requested_for);
        gr.setValue('u_requested_for_email','beth.anglin@example.com');
        gr.setValue('u_when_needed', gs.daysAgo(-7));
        gr.insert();
        response.NeeditTicket = gr.number;
        response.requestor = gr.getDisplayValue('u_requested_for');
    } catch (error) {
        response.err = error;
    }


})(request, response);
