sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var username = request.pathParams.user;
	var ninumber=request.queryParams.number;
	var queryparm=request.queryParams.ExplicitQuery;

    var needitgr = new GlideRecord('x_58872_needit_needit');
    needitgr.addQuery('u_requested_for.name', username);
	if(ninumber)
		{
			needitgr.addQuery('number',ninumber);
		}
	if(queryparm)
		{
			needitgr.addEncodedQuery(queryparm);
		}
    needitgr.query();
    var result = [];
    while (needitgr.next()) {
		var needobj={};
		needobj.number= needitgr.getValue('number');
		needobj.requestor=needitgr.u_requested_for.name;
        //result.push({'number': needitgr.getValue('number'),'requestor':needitgr.getDisplayValue('u_requested_for')});
		result.push(needobj);

    }
    response.setBody(result);
	//return result;
})(request, response);
