sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var neednumber = request.pathParams.number;

    var body = request.body.data.detail;
    var shortdes = body.short_description;
    var assignment_group = body.assignment_group;
    if (!(shortdes)) {
        var err = new sn_ws_err.ServiceError();
        err.setStatus(400);
        err.setMessage('Short description cannot be null');
        err.setDetail('Short description cannot be null');
        return err;
    } else {
        var gr = new GlideRecord('x_58872_needit_needit');
        gr.addQuery('number', neednumber);
        gr.query();
        while (gr.next()) {
            gr.setValue('short_description', shortdes + gr.short_description);
            gr.assignment_group.setDisplayValue(assignment_group);
			try{
            gr.update();
			}catch(exc){response.setBody(exc.message);}
            var resp = {
                'number': gr.number, //resp['number']=gr.number
                'requested for': gr.u_requested_for //resp['requested for']=gr..u_requested_for
            };
            response.setBody(resp);
            //
        }
    }
    //return resp;
})(request, response);
