sysevent_in_email_action.script:
(function runAction( /*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {
    try {
        gs.info('started processing incoming needit mail' + "\n email body: " + email.body.needit + "\n event parm: " + event.parm1);
        current.u_needit.setDisplayValue(email.body.needit);
        current.short_description = email.subject+":"+ email.body.assignee;		
        current.assigned_to.setDisplayValue(email.body.assignee);
        if (current.canCreate())
			{
            current.insert();
			}
        gs.info('finished processing incoming needit mail');
    } catch (ex) {
        gs.info('caught exception :' + ex);
    }
})(current, event, email, logger, classifier);
